import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './UserInputScreen.css';
import config from '../../config';

function UserInputScreen() {
    const location = useLocation();
    const { menuList: initialMenuList, selectedStore, budget } = location.state;
    const [messages, setMessages] = useState([]);
    const [menuList, setMenuList] = useState(initialMenuList);
    const [recommendedMenus, setRecommendedMenus] = useState([]);
    const [questionCount, setQuestionCount] = useState(0);
    const [stage, setStage] = useState('initial');
    const [selectedMenu, setSelectedMenu] = useState('');
    const [userLocation, setUserLocation] = useState('');
    const [availableLocations] = useState([
        '법대후문', '고대정문앞', '고대사거리', '정대후문', '참살이길', 
        '안암로터리', '제기동', '이공계', '교내'
    ]);

    useEffect(() => {
        const storeTypeKorean = selectedStore === 'restaurant' ? '밥집' : selectedStore === 'cafe' ? '카페' : '술집';
        addMessage(`안녕하세요! ${storeTypeKorean}에서 예산 ${budget}원으로 메뉴를 추천드리겠습니다.`, false);
        askQuestion();
    }, []);

    const addMessage = (content, isUser) => {
        setMessages(prev => [...prev, { content, isUser }]);
    };

    const askQuestion = async (userInput = '') => {
        try {
            const response = await fetch(`${config.apiUrl}/api/menu-selection`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    menu_list: menuList,
                    user_response: userInput,
                    question_count: questionCount,
                    previous_responses: messages.filter(msg => msg.isUser).map(msg => msg.content)  // 이전 사용자 응답들을 전송
                }),
            });
            const data = await response.json();
    
            if (data.success) {
                if (data.next_question) {
                    addMessage(data.next_question, false);
                    setQuestionCount(prev => prev + 1);
                } else if (data.recommended_menus) {
                    setRecommendedMenus(data.recommended_menus);
                    addMessage(`추천 메뉴: ${data.recommended_menus.join(', ')}`, false);
                    addMessage('위 메뉴 중에서 선택하고 싶은 메뉴를 입력해주세요. 없다면 "다시 추천"이라고 입력해주세요.', false);
                    setStage('menu_selection');
                }
            }
        } catch (error) {
            console.error('Error asking question:', error);
            addMessage('죄송합니다. 오류가 발생했습니다.', false);
        }
    };

    const handleSendMessage = async () => {
        const userInput = document.getElementById('userInput').value.trim();
        if (userInput) {
            addMessage(userInput, true);
            document.getElementById('userInput').value = '';

            if (stage === 'menu_selection') {
                if (userInput.toLowerCase() === '다시 추천') {
                    await askQuestion('다른 메뉴를 추천해주세요.');
                } else if (recommendedMenus.includes(userInput)) {
                    setSelectedMenu(userInput);
                    addMessage(`${userInput} 메뉴를 선택하셨습니다.`, false);
                    addMessage(`법대후문, 고대정문앞, 고대사거리, 정대후문, 참살이길, 안암로터리, 제기동, 이공계, 교내 중 선호하는 특정 지역이 있습니까?`, false);
                    setStage('location_selection');
                } else {
                    addMessage('선택하신 메뉴가 추천 목록에 없습니다. 다시 선택해주세요.', false);
                }
            } else if (stage === 'location_selection') {
                handleLocationSelection(userInput);
            } else if (stage === 'preferences') {
                await recommendRestaurants(userInput);
            } else {
                await askQuestion(userInput);
            }
        }
    };

    const handleLocationSelection = (userInput) => {
        if (availableLocations.includes(userInput)) {
            setUserLocation(userInput);
            addMessage(`선택하신 지역은 "${userInput}"입니다.`, false);
            addMessage('좋아요! 해당 지역을 바탕으로 식당을 추천해줄게요. 만족도, 분위기, 서비스, 맛, 양, 위생과 관련해서 의견제시해주세요.', false);
            setStage('preferences');
        } else {
            addMessage('죄송합니다. 선택하신 지역은 옵션에 없습니다. 다음 중에서 선택해주세요: ' + availableLocations.join(', '), false);
        }
    };

    const recommendRestaurants = async (preferences) => {
        try {
            const response = await fetch(`${config.apiUrl}/api/recommend-restaurants`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    selected_menu: selectedMenu,
                    store_type: selectedStore,
                    max_price: budget,
                    location: userLocation,
                    user_preferences: preferences
                }),
            });
            const data = await response.json();

            if (data.success) {
                addMessage(data.recommended_restaurants, false);
                setStage('completed');
            } else {
                addMessage('식당 추천 중 오류가 발생했습니다.', false);
            }
        } catch (error) {
            console.error('Error recommending restaurants:', error);
            addMessage('죄송합니다. 식당 추천 중 오류가 발생했습니다.', false);
        }
    };

    return (
        <div className="chat-container">
            <header className="chat-header">쿠잇나우</header>
            <div className="chat-messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.isUser ? 'user' : 'bot'}`}>
                        {!msg.isUser && <div className="circle bot"></div>}
                        <div className="message-bubble">{msg.content}</div>
                    </div>
                ))}
            </div>
            <div className="chat-input-container">
                {stage !== 'completed' && (
                    <>
                        <input
                            type="text"
                            id="userInput"
                            placeholder="메시지를 입력해주세요."
                            className="chat-input"
                            onKeyPress={(e) => {
                                if (e.key === 'Enter') handleSendMessage();
                            }}
                        />
                        <button onClick={handleSendMessage} className="send-button">
                            ▶
                        </button>
                    </>
                )}
            </div>
        </div>
    );
}

export default UserInputScreen;
