import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserLimitScreen2.css';

function UserLimitScreen2() {
    const [selectedStore, setSelectedStore] = useState(null);
    const [budget, setBudget] = useState('');
    const navigate = useNavigate();

    // 가게 선택 핸들러
    const handleStoreClick = (store) => {
        setSelectedStore(store);
    };

    // 예산 입력 핸들러
    const handleBudgetChange = (e) => {
        setBudget(e.target.value);
    };

    // "다음으로" 버튼 클릭 시 UserInputScreen으로 이동 및 API 호출
    const handleNextClick = async () => {
        if (selectedStore && budget >= 3000) {
            // 서버로 데이터 전송
            try {
                const response = await fetch('/api/filter_menus', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        store_type: selectedStore,
                        max_price: budget,
                    }),
                });

                const data = await response.json();

                if (data.success) {
                    console.log('필터링된 메뉴 목록:', data.menu_list);
                    // 데이터를 받아서 다음 페이지로 넘길 수 있음
                    navigate('/user-input');
                } else {
                    console.error('메뉴 가져오기 오류:', data.error);
                }
            } catch (error) {
                console.error('API 호출 오류:', error);
            }
        }
    };

    return (
        <div className="user-limit-container">
            <div className="logo-section">
                <h1>쿠잇나우 로고</h1>
            </div>

            {/* 가게 종류 선택 */}
            <div className="section">
                <h2>가게 종류</h2>
                <p>찾고 있는 가게의 종류에 따라 질문이 달라져요.</p>
                <div className="store-buttons">
                    {['밥집', '카페', '술집'].map((store) => (
                        <button
                            key={store}
                            className={`store-button ${selectedStore === store ? 'selected' : ''}`}
                            onClick={() => handleStoreClick(store)}
                        >
                            {store}
                        </button>
                    ))}
                </div>
            </div>

            {/* 예산 입력 */}
            <div className="section">
                <h2>식사 예산</h2>
                <p>다양한 메뉴 추천을 위해<br/>4000원 이상을 입력하는 걸 권장해요.</p>
                <div className="budget-input-container">
                    <input
                        type="number"
                        placeholder="예산을 입력해주세요."
                        value={budget}
                        onChange={handleBudgetChange}
                    />
                    <span>원</span>
                </div>
            </div>

            {/* 다음으로 버튼 */}
            <button
                className={`user-limit-next-button ${selectedStore && budget >= 3000 ? 'active' : ''}`}
                onClick={handleNextClick}
                disabled={!selectedStore || budget < 3000}
            >
                다음으로
            </button>
        </div>
    );
}

export default UserLimitScreen2;
